script:
frappe.ui.form.on("Item", {
	width_in: function(frm, cdt, cdn) {
		var item = frappe.model.get_doc(cdt, cdn);
		if (item.width_in >= 0) {
			var width_cm = Math.round(item.width_in * 2.54 * 2)/2;
			frappe.model.set_value(cdt,cdn,"width_cm",width_cm);
		}
if (item.gsm > 170) {
			var net_weight_num = (item.width_cm * item.length_cm * item.gsm/100000);	    
			var net_weight = 	Number(Math.round(net_weight_num+'e3')+'e-3').toFixed(1);
			frappe.model.set_value(cdt,cdn,"net_weight",net_weight);
		} else
		{
			var net_weight_num = item.width_cm * item.length_cm * item.gsm/50000;
			var net_weight = 		Number(Math.round(net_weight_num+'e3')+'e-3').toFixed(1);
			frappe.model.set_value(cdt,cdn,"net_weight",net_weight);
		}
 	}
});

